mapper.select:
select count(favno) as cnt
from favorite
where usersno = #{usersno} and storeno = #{storeno}

mapper.update:
update favorite
set favcheck = favcheck + 1
where usersno = #{usersno} and storeno = #{storeno}

update favorite
set favcheck = 0
where usersno = #{usersno} and storeno = #{storeno}

mapper.select:
select favno, usersno, storeno, favcheck
from favorite
where usersno=#{usersno} and storeno=#{storeno}
