mapper.select:
SELECT  reviewno, contents, file1, file1saved, thumb, score, rdate, storeno
FROM review
WHERE reviewno=#{reviewno}

SELECT  reviewno, contents, file1, file1saved, thumb, score, rdate, storeno
FROM review
WHERE storeno=#{storeno}

mapper.update:
UPDATE review
SET contents=#{contents}, file1=#{file1}, file1saved=#{file1saved}, thumb=#{thumb}
WHERE reviewno = #{reviewno}
